import React, { useState, useEffect } from "react";
import Quiz from "./Components/Quiz.js";
import IntroPage from "./Components/IntroPage.js";
import { nanoid } from "nanoid";
import { shuffle } from "./helperFunctions.js";
export default function App() {
	const [quizData, setQuizData] = useState([]);
	const [isLoaded, setIsLoaded] = useState(false);
	function getQuestions() {
		fetch("https://opentdb.com/api.php?amount=5&type=multiple")
			.then(res => res.json())
			.then(data =>
				setQuizData(
					data.results.map(question => {
						const questionId = nanoid();
						const allAnswers = question.incorrect_answers.concat(
							question.correct_answer
						);
						const newAnswers = allAnswers.map(answer => ({
							answer: answer,
							answerId: nanoid(),
							isSelected: false,
						}));
						return {
							correctAnswer: question.correct_answer,
							question: question.question,
							answers: shuffle(newAnswers),
							questionId: questionId,
						};
					})
				)
			);
		setIsLoaded(true);
	}
	console.log(quizData);
	function selectAnswer(quiz, event) {
		setQuizData(oldQuizData => {
			oldQuizData.map(question => {
				const selectedAnswers = question.answers.map(answer => {
					const answerId = answer.answerId;
					console.log(answerId === event.target.id ? "eşitlendi" : "");
					return {
						...answer,
						isSelected: answerId === event.target.id ? true : false,
					};
				});
				return event.target.dataset.questionId === question.questionId
					? { ...question, answers: selectedAnswers }
					: question;
			});
		});
		console.log(quizData);
	}
	function question() {
		return quizData.map(quiz => (
			<Quiz
				key={quiz.questionId}
				question={quiz.question}
				answers={quiz.answers}
				correctAnswer={quiz.correctAnswer}
				handleClick={event => selectAnswer(quiz, event)}
			/>
		));
	}

	return (
		<div>
			<div className="quiz-container" id="quiz-container">
				{!isLoaded && <IntroPage handleClick={getQuestions} />}
				{isLoaded && <div className="quizes">{question()}</div>}
				{isLoaded && <button className="submit-btn">SUBMIT</button>}
			</div>
		</div>
	);
}
