import React, { useState } from "react";
export function htmlDecode(input) {
	var doc = new DOMParser().parseFromString(input, "text/html");
	return doc.documentElement.textContent;
}
export default function Quiz(props) {
	console.log(htmlDecode(props.question.answer[0]));
	const answerButtons = props.question.answer.map(answer => (
		<button>{htmlDecode(answer)}</button>
	));
	return (
		<div className="quiz">
			<div className="question">{props.question.question.question}</div>
			<div className="answers">{answerButtons}</div>
			<hr />
		</div>
	);
}
