import React, { useState } from "react";

export default function Quiz(props) {
	const answerButton = props.question.question.answer.map(answer => (
		<button>{answer}</button>
	));
	return (
		<div className="quiz">
			<div className="question">{props.question.question.question}</div>
			<div className="answers">{answerButton}</div>
			<hr />
		</div>
	);
}
