import React, { useState } from "react";

export default function Quiz(props) {
	console.log(props.question);
	const answerButtons = props.question.answer.map(answer => <button></button>);
	return (
		<div className="quiz">
			<div className="question">{props.question.question.question}</div>
			<div className="answers">{answerButtons}</div>
			<hr />
		</div>
	);
}
