import React, { useState, useEffect } from "react";
import Quiz from "./Components/Quiz.js";
import IntroPage from "./Components/IntroPage.js";
import { nanoid } from "nanoid";
import { shuffle } from "./helperFunctions.js";
export default function App() {
	const [quizData, setQuizData] = useState({});
	const [isLoaded, setIsLoaded] = useState(false);
	const [check, setCheck] = useState(false);
	const quizEls = "";
	function getQuestions() {
		setCheck(true);
		fetch("https://opentdb.com/api.php?amount=5&type=multiple")
			.then(res => res.json())
			.then(data =>
				setQuizData(
					data.results.map(question => {
						const questionId = nanoid();
						const allAnswers = question.incorrect_answers.concat(
							question.correct_answer
						);
						const newAnswers = allAnswers.map(answer => ({
							answer: answer,
							id: questionId,
							isSelected: false,
						}));
						return {
							correctAnswer: question.correct_answer,
							question: question.question,
							answer: shuffle(newAnswers),
							questionId: questionId,
						};
					})
				)
			);
		setIsLoaded(true);
	}
	function question() {
		return quizData.map(quiz => (
			<Quiz
				key={quiz.id}
				question={quiz}
				handleClick={() => selectAnswer(quiz, event)}
			/>
		));
	}
	function selectAnswer(event, quiz) {
		console.log(quiz);
	}
	return (
		<div>
			<div className="quiz-container" id="quiz-container">
				{!check && !quizData[0] && <IntroPage handleClick={getQuestions} />}
				{check && quizData[0] && <div className="quizes">{question()}</div>}
				{check && quizData[0] && <button className="submit-btn">SUBMIT</button>}
			</div>
		</div>
	);
}
