import React, { useState } from "react";
import { htmlDecode } from "../helperFunctions";
export default function Quiz(props) {
	const answerButtons = props.question.answer.map(answer => (
		<button
			className="answer-btn"
			onClick={props.handleClick(function (event) {
				return props.selectAnswer(event, answer.answer);
			})}
		>
			{htmlDecode(answer.answer)}
		</button>
	));
	return (
		<div className="quiz">
			<div className="question">{htmlDecode(props.question.question)}</div>
			<div className="answers">{answerButtons}</div>
			<hr />
		</div>
	);
}
